import '../App.css';
import Offer from '../components/offer';
import { useEffect, useState } from 'react';
import Header from '../components/header';
import Showcase from '../components/showcase';

function Marketplace() {
  const [offers, setOffers] = useState([]);
  const [showcase, setShowcase] = useState(null);
  const [loadedOffers, setLoadedOffers] = useState([]); // Track which offers have been loaded

  useEffect(() => {
    async function fetchOffers() {
      const response = await fetch('/api/getAllOffers', {
        method: 'GET',
      });
      const data = await response.json();
      setOffers(data);
      setLoadedOffers(new Array(data.length).fill(false)); // Initialize loaded state for each offer
    }
    fetchOffers();
  }, []);

  function closeShowcase() {
    setShowcase(null);
  }

  function showcaseOffer(offer) {
    setShowcase(offer);
  }

  // Trigger fade-in effect after a slight delay
  const handleOfferLoad = (index) => {
    setTimeout(() => {
      setLoadedOffers((prev) => {
        const newLoadedOffers = [...prev];
        newLoadedOffers[index] = true;
        return newLoadedOffers;
      });
    }, 100 * index); // Delay each offer's fade-in for a more staggered effect
  };

  return (
    <div className="Marketplace">
      <Header />
      <div className="scrollable">
        <Showcase offer={showcase} close={closeShowcase} />
        <div className="offerList">
          {offers.map((offer, index) => (
            <span key={offer.id}>
              <Offer
                offer={offer}
                func={showcaseOffer}
                style={{
                  opacity: loadedOffers[index] ? 1 : 0,
                  transition: 'opacity 500ms ease-in',
                }}
                onLoad={() => handleOfferLoad(index)} // Trigger fade-in on offer load
              />
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Marketplace;
