import '../App.css';
import Offer from '../components/offer';
import { useEffect, useState } from 'react'
import Header from '../components/header';
import Showcase from '../components/showcase';

function Marketplace() {

	const [offers, setOffers] = useState([])
	const [showcase, setShowcase] = useState(null)

	const [loading, setLoading] = useState(0);


	useEffect(() => {
		async function fetchOffers() {
			const response = await fetch('/api/getAllOffers', {
				method: 'GET'
			})
			const data = await response.json()
			setOffers(data)
			setLoading(1);
		}
		fetchOffers()
	}, [])

	function closeShowcase() {
		setShowcase(null)
	}

	function showcaseOffer(offer) {
		setShowcase(offer)
	}

	return (
		<div className="Marketplace">
			<Header />
			<div className='scrollable'>
				<Showcase offer={showcase} close={closeShowcase} />
				<div className='offerList' style={{opacity: loading, transition: 'opacity 500ms ease-in'}}>
					{offers.map((offer) =>
						<span key={offer.id}>
							<Offer offer={offer} func={showcaseOffer} />
						</span>
					)}
				</div>
			</div>
		</div>
	);
}

export default Marketplace;
