import '../App.css';
import Offer from '../components/offer';
import { useEffect, useState } from 'react'
import Header from '../components/header';
import Showcase from '../components/showcase';
import InfoBox from '../components/infoBox';

function Marketplace() {

	const [offers, setOffers] = useState([])
	const [showcase, setShowcase] = useState(null)
	const [finance, setFinance] = useState(0)
	const [loading, setLoading] = useState(0);

	const [infoBoxMessage, setInfoBoxMessage] = useState('');
    const [infoBoxSuccess, setInfoBoxSuccess] = useState(false);

	useEffect(() => {
		async function fetchOffers() {
		  try {
			const response = await fetch('/api/getAllOffers', { method: 'GET' });
			const data = await response.json();
	  
			// Separate offers with and without images
			const offersWithImages = data.filter((offer) => offer.image);
			const offersWithoutImages = data.filter((offer) => !offer.image);
	  
			// Fetch filenames for offers without images in parallel
			const filenamePromises = offersWithoutImages.map((offer) =>
			  fetch(`/api/get_offer_filename_from_id?offer_id=${offer.id}`, { method: 'GET' })
				.then((res) => res.json())
				.then((filenameData) => ({
				  ...offer,
				  filename: filenameData.filename,
				}))
				.catch(() => ({ ...offer, filename: null }))
			);
	  
			const offersWithFilenames = await Promise.all(filenamePromises);
	  
			// Fetch images for offers with filenames in parallel
			const imagePromises = offersWithFilenames.map((offer) =>
			  offer.filename
				? fetch(`/api/get_image.php?filename=${offer.filename}`)
					.then((res) => res.json())
					.then(({ image }) => ({
					  ...offer,
					  image: image ? `data:image/jpeg;base64,${image}` : '',
					}))
					.catch(() => ({ ...offer, image: '' }))
				: Promise.resolve(offer)
			);
	  
			const finalOffers = await Promise.all(imagePromises);
	  
			// Combine offers with images and newly fetched offers
			const allOffers = [...offersWithImages, ...finalOffers];
			setOffers(allOffers);
			setLoading(1);
		  } catch (error) {
			console.error('Error fetching offers:', error);
		  }
		}
	  
		async function fetchUserData() {
		  try {
			const response = await fetch('/api/Profile', {
			  headers: { 'Content-Type': 'application/json' },
			});
	  
			if (response.ok) {
			  const data = await response.json();
			  setFinance(data.money);
			}
		  } catch (error) {
			console.error('Error fetching user data:', error);
		  }
		}
	  
		// Insert the custom function script
		const myFunction = `
		  async function buyOffer(id, balance, price) {
			if (price > balance) {
			  alert('Hello from the custom function!');
			  return;
			}
			const response = await fetch('/api/buy', {
			  method: 'POST',
			  headers: { 'Content-Type': 'application/json' },
			  body: JSON.stringify({ id })
			});
	  
			return response.ok;
		  }
		`;
	  
		const script = document.createElement('script');
		script.innerHTML = myFunction;
		document.head.appendChild(script);
	  
		fetchOffers();
		fetchUserData();
	  }, []);

	async function buyRequest(e){
        e.preventDefault()

		const offer = showcase


        if(await window.buyOffer(offer.id, finance, offer.price)){
			successBox('Successfully bought!', true)
		} else {
			successBox('Something went wrong!', false)
		}
    }

	async function informationBox(obj, message, success, delay=2000){

		function timeout(delay) {
			return new Promise( res => setTimeout(res, delay) );
		}

        const infoBoxObj = obj
        infoBoxObj.style.display = 'flex'

        setInfoBoxSuccess(success)
        setInfoBoxMessage(message)

        infoBoxObj.style.display = 'flex'

        await timeout(delay);
        infoBoxObj.style.opacity = 0
        await timeout(500);
        infoBoxObj.style.zIndex = -100
        await timeout(500);
        infoBoxObj.style.display = 'none'
		infoBoxObj.style.opacity = 1
		infoBoxObj.style.zIndex = 100
    }

	async function successBox(message, state){
        const infoBoxObj = document.getElementById('infoBox')
		informationBox(infoBoxObj, message, state, 2000)
    }

	function closeShowcase() {
		setShowcase(null)
	}

	function showcaseOffer(offer) {
		setShowcase(offer)
	}

	return (
		<div className="Marketplace">
			<Header cash={finance} />
			<InfoBox message={infoBoxMessage} success={infoBoxSuccess}/>
			<div className='scrollable'>
				<Showcase offer={showcase} close={closeShowcase} balance={finance} buyFunc={buyRequest}/>
				<div className='offerList' style={{ opacity: loading, transition: 'opacity 300ms ease-in' }}>
					{offers.map((offer) =>
						<span key={offer.id}>
							<Offer offer={offer} func={showcaseOffer} /> 
							{/* <Offer offer={{ ...offer, image: `/uploads/${offer.image}` }} func={showcaseOffer} /> */}
						</span>
					)}
				</div>
			</div>
		</div>
	);
}

export default Marketplace;
