import '../App.css';
import Offer from '../components/offer';
import { useEffect, useState, useRef } from 'react';
import Header from '../components/header';
import Showcase from '../components/showcase';

function Marketplace() {
  const [offers, setOffers] = useState([]);
  const [showcase, setShowcase] = useState(null);
  const offerRefs = useRef([]); // Create a reference array to track the offers

  useEffect(() => {
    async function fetchOffers() {
      const response = await fetch('/api/getAllOffers', {
        method: 'GET',
      });
      const data = await response.json();
      setOffers(data);
    }
    fetchOffers();
  }, []);

  useEffect(() => {
    // Trigger fade-in for each offer after they are loaded
    if (offers.length) {
      offers.forEach((offer, index) => {
        setTimeout(() => {
          // Fade in each offer by changing opacity to 1
          if (offerRefs.current[index]) {
            offerRefs.current[index].style.opacity = 1;
          }
        }, 100 * index); // Stagger the fade-in effect
      });
    }
  }, [offers]);

  function closeShowcase() {
    setShowcase(null);
  }

  function showcaseOffer(offer) {
    setShowcase(offer);
  }

  return (
    <div className="Marketplace">
      <Header />
      <div className="scrollable">
        <Showcase offer={showcase} close={closeShowcase} />
        <div className="offerList">
          {offers.map((offer, index) => (
            <span key={offer.id}>
              <Offer
                ref={(el) => (offerRefs.current[index] = el)} // Store reference to each offer
                offer={offer}
                func={showcaseOffer}
                style={{
                  opacity: 0, // Start with opacity 0
                  transition: 'opacity 500ms ease-in-out', // Smooth transition for opacity
                }}
              />
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Marketplace;
